    color: $custom-text-color !important; /* Custom text color */
    font-family: fantasy; /* Apply fantasy font family globally */
}

.navbar a {
    height: 9vh;
    font-family: fantasy; /* Apply fantasy font family */
}

/* Navbar link text color and font size */
.nav-link {
    color: #FFFFFF; 
    font-size: 13px; /* Adjust font size as needed */
}

.nav-item {
    margin-top: 20px;
    font-family: fantasy; /* Apply fantasy font family */
    color: #FFFFFF; /* Optional: white color for text */
}

/* Navbar link hover effect */
.navbar a:hover {
    color: $hover-text-color !important; /* Yellow text on hover */
}

/* Button Styles with Black Background and Light Orange Text */
.btn, 
.btn-primary, 
.btn-default, 
.btn-warning, 
.btn-primary-light, 
.font-md, 
.btn-sm {
    background-color: $button-bg-color !important; /* Black button background */
    color: $button-text-color !important; /* Light orange button text */
    border-color: $button-bg-color !important; /* Black border */
}

/* Button hover effect */
.btn:hover, 
.btn-primary:hover, 
.btn-warning:hover, 
.btn-primary-light:hover {
    background-color: $button-hover-bg-color !important; /* Light orange on hover */
    color: #ffffff !important; /* Change text color to white on hover */
    border-color: $button-hover-bg-color !important; /* Light orange border on hover */
}

/* Background color for the rest of the website */
body {
    background-color: #ffffff !important; /* White background */
    color: $custom-text-color !important; /* Custom text color for the page */
}


