custom_overrides:
@import 'node_modules/bootstrap/scss/bootstrap'; // Import Bootstrap SCSS

$color2: #FFFFFF; // Default color for text

/* Header with Brown Background and Black Text */
.navbar, 
.navbar-default {
    background-color: #AC8968 !important; /* Brown background */
    color: $color2 !important; /* Text color */
}

.navbar a, 
.navbar-default a {
    color: $color2 !important; /* Black text links */
}

.navbar a:hover, 
.navbar-default a:hover {
    color: #FFD700 !important; /* Yellow on hover */
}

/* Dropdown Menu with Brown Background */
.dropdown-menu, 
.dropdown-menu-right, 
.show {
    background-color: #AC8968 !important; /* Brown background */
}

/* Buttons with Brown Background and White Text */
.btn, 
.btn-primary, 
.btn-default, 
.btn-warning {
    background-color: #AC8968 !important; /* Brown button background */
    color: $color2 !important; /* Text color */
    border-color: #AC8968 !important; /* Border color */
}

.btn:hover, 
.btn-primary:hover, 
.btn-warning:hover {
    background-color: #C4A484 !important; /* Lighter brown on hover */
    color: $color2 !important; /* Text color */
    border-color: #FFD700 !important; /* Yellow border */
}

/* Body Background */
body {
    background-color: #f8f8f8 !important; /* Light background */
}

/* Dropdown Search Menu Styles */
#dropdownMenuSearch {
    border: 6px solid #AC8968 !important;
    border-radius: 4px; /* Rounded corners */
    background-color: #ADD8E6; /* Light blue background */
}

/* Hover Effects for Cards and Rows */
.card-img:hover, 
.list-row:hover {
    transform: scale(1.1); /* Zoom in */
    box-shadow: 0px 10px 20px rgba(0, 0, 0, 0.2); /* Shadow */
}

/* Borders for Card and List Row */
.card, 
.list-row, 
.account-info, 
.website-list {
    border: 2px solid #AC8968 !important; /* Brown border */
}

/* Icon Colors */
.desk-icons .module-icons .icon {
    color: $color2 !important; /* White icons */
}

.module-icons-selling .icon {
    color: #007bff !important; /* Blue for Selling module */
}

.module-icons-accounting .icon {
    color: #28a745 !important; /* Green for Accounting module */
}

