custom_overrides:
@import 'node_modules/bootstrap/scss/bootstrap';

$color2: #FFFFFF; // Default color for text
$highlight-yellow: #FFD700; // Yellow color for hover states and highlights
$light-brown: #AC8968; // Brown background and borders
$hover-brown: #C4A484; // Lighter brown for hover effects
$bg-light: #f8f8f8; // Light background for the body

/* Header with Brown Background and White Text */
.navbar, .navbar-default {
    background-color: $light-brown !important; /* Brown background */
    color: $color2 !important; /* White text */
}

.navbar a, .navbar-default a {
    color: $color2 !important; /* White text links */
}

.navbar a:hover, .navbar-default a:hover {
    color: $highlight-yellow !important; /* Yellow on hover */
}

/* Dropdown Menu with Brown Background */
.dropdown-menu, .dropdown-menu-right, .show {
    background-color: $light-brown !important; /* Brown background */
}

/* Buttons with Brown Background and White Text */
.btn, .btn-primary, .btn-default, .btn-warning {
    background-color: $light-brown !important; /* Brown button background */
    color: $color2 !important; /* White text */
    border-color: $light-brown !important; /* Brown border */
}

.btn:hover, .btn-primary:hover, .btn-warning:hover {
    background-color: $hover-brown !important; /* Lighter brown on hover */
    color: $color2 !important; /* White text */
    border-color: $highlight-yellow !important; /* Yellow border */
}

/* Body Background */
body {
    background-color: $bg-light !important; /* Light background */
}

/* Dropdown Search Menu Styles */
#dropdownMenuSearch {
    border: 6px solid $light-brown !important; /* Brown border */
    border-radius: 4px; /* Rounded corners */
    background-color: #ADD8E6; /* Light Blue background */
}

/* Hover Effects for Cards and Rows */
.card-img:hover, .list-row:hover {
    transform: scale(1.1); /* Zoom in */
    box-shadow: 0px 10px 20px rgba(0, 0, 0, 0.2); /* Shadow */
}

/* Card and List Row Borders */
.card, .list-row, .account-info, .website-list {
    border: 2px solid $light-brown !important; /* Brown border */
}

/* Icon Colors */
.desk-icons .module-icons .icon {
    color: $color2 !important; /* White icons */
}

.module-icons-selling .icon {
    color: #007bff !important; /* Blue for Selling module */
}

.module-icons-accounting .icon {
    color: #28a745 !important; /* Green for Accounting module */
}

